import moment from 'moment';
import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet } from 'react-native'

const WeatherItem = ({ title, value, unit }) => {

    return (
        <View style={styles.weatherItem}>
            <Text style={styles.weatherItemTitle}>{title}</Text>
            <Text style={styles.weatherItemTitle}>{value}{unit}</Text>
        </View>)
}

const DateTime = () => {

    const [currentDate, setCurrentDate] = useState('');

    useEffect(() => {
        setInterval(() => {
            var date = moment()
                .format(' hh:mma');
            setCurrentDate(date);
        }, 1000)
    }, []);

    return (
        <View style={styles.container}>
            <View>
                <View>
                    {/* <Text style={styles.heading}>12:30am</Text> */}
                    <Text style={styles.heading}>{currentDate}</Text>
                </View>
                <View>
                    <Text style={styles.subheading}>Monday, 7, June</Text>
                </View>
                <View style={styles.weatherItemContainer}>
                    <WeatherItem title='Humidity' value={79} unit='%' />
                    <WeatherItem title='Pressure' value={1000} unit='hPa' />
                    <WeatherItem title='Sunrise' value='06:00' unit='am' />
                    <WeatherItem title='Sunset' value='06:30' unit='pm' />
                </View>
            </View>
            <View style={styles.rightAlign}>
                <Text style={styles.timezone}>Ukraine, Kyiv</Text>
                <Text style={styles.latlong}>4.22 EN 50e</Text>
            </View>
        </View>
    )
}

export default DateTime

const styles = StyleSheet.create({
    container: {
        flex: 1.5,
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: 20
    },
    heading: {
        fontSize: 45,
        color: 'white',
        fontWeight: '100'
    },
    subheading: {
        fontSize: 25,
        fontWeight: "300",
        color: '#eee'
    },
    rightAlign: {
        textAlign: 'right',
        marginTop: 20
    },
    timezone: {
        fontSize: 20,
        color: 'white'
    },
    latlong: {
        fontSize: 16,
        fontWeight: '700',
        color: 'white'
    },
    weatherItemContainer: {
        backgroundColor: '#18181b99',
        borderRadius: 10,
        padding: 10,
        marginTop: 10
    },
    weatherItem: {
        flexDirection: 'row',
        justifyContent: 'space-between'
    },
    weatherItemTitle: {
        color: '#eee',
        fontSize: 14,
        fontWeight: '100'
    }
})
