import React, { useEffect, useState } from 'react'
import Background from './background/Background'
import { View, StyleSheet, Image, Text, Button } from 'react-native'


const Main = ({ weatherData }) => {

    const { weather,
        name,
        wind: { speed }
    } = weatherData;
    const [{ main }] = weather;

    return (
        <View style={styles.container}>
            <Background>
            </Background>
            <View>
                <Text style={styles.text}>Lon: {temp}</Text>
                <Text style={styles.text}>Lat: {name}</Text>
            </View>
        </View>
    )
}

export default Main

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    weatherContainer: {
        backgroundColor: 'rgba(141, 141, 145,0.5)',
        height: '100px',
        width: '100px',
    },
    text: {
        fontSize: 36,
    }

})
