import React from 'react'
import { StyleSheet } from 'react-native'

function Background() {
    return (
        <LinearGradient
            colors={['rgb(24,33,64)', 'rgb(51,53,114)', 'rgb(55,54,125)', 'rgb(68,58,143)', 'rgb(98,70,168)', "rgb(106,75,170)", 'rgb(143,67,170)']}
            style={styles.background}
        />
    )
}

export default Background

const styles = StyleSheet.create({
    background: {
        position: 'absolute',
        height: '100%',
        width: '100%'
    }, 
})
