import React from 'react'
import {View, ScrollView, Image, Text, StyleSheet} from 'react-native'

const WeatherScroll = () => {
  return (
    <ScrollViewView style={styles.scrollView}>
        <CurrentTempEl></CurrentTempEl>
    </ScrollViewView>
  )
}

export default WeatherScroll

const CurrentTempEl = () => {
    return (
        <View>
            <Image style={styles.img} source={require('../assets/sunny.png')}/>
            <View>
                <Text>Sunday</Text>
                <Text>Night - 28&#176;C</Text>
                <Text>Day - 35&#176; C</Text>
            </View>
        </View>
    )
}
const styles = StyleSheet.create({
    img:{
        width: 150,
        height: 150
    },
    scrollView:{
        backgroundColor: '18181b99',
    }
})
