import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet, Image,ImageBackground, Dimensions, StatusBar } from 'react-native'
import SearchBar from './SearchBar';
import { haze, rainy, snow, sunny } from '../assets/backgroundImages/index'

export default function Weather({ weatherData }) {
    const [background, setBackground] = useState(null)
    const { weather,
        name,
        main: { temp, humidity } } = weatherData;
    const [{ main }] = weather;
    const icon = weatherData.weather[0].icon;    

    React.useEffect(() => {
        setBackground(getBackground(main));
    }, [weatherData])

    function getBackground(weather) {
        if (weather === "Snow") return sunny
        if (weather === "Haze") return sunny
        if (weather === "Rain") return sunny
        if (weather === "Clear") return sunny   
        
        return rainy;
    }

    return (
        <View style={styles.container}>
            <ImageBackground
                source={background}
                style={styles.backgroundImg}
                resizeMode='cover'>

                <SearchBar />
                <View style={{ alignItems: 'center' }}>
                    <Text style={{...styles.headerText}}>{name}</Text>
                    <Image ></Image>
                </View>
            </ImageBackground>
        </View>
    )
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },
    headerText: {
        marginTop: 40,
        fontSize: 36
    },
    backgroundImg:{
        flex:1,
        width:Dimensions.get('screen').width
    }
});
