import React, { useEffect, useState } from 'react'
import Background from './background/Background'
import { Image, View, StyleSheet, Text, Button } from 'react-native'
import { LinearGradient } from 'expo-linear-gradient';


const Main = ({ data }) => {
    const { weather,
        name,
        main: { temp, humidity },
        wind: { speed }
    } = data;
    const [{ main, description, icon }] = weather;
    // const[{description}] = weather;

    return (
        <LinearGradient colors={['rgb(24,33,64)', 'rgb(51,53,114)', 'rgb(55,54,125)', 'rgb(68,58,143)', 'rgb(98,70,168)', "rgb(106,75,170)", 'rgb(143,67,170)']}
        style={{flex:1}}>
            <View>
                <Image source={require('../../assets/cloud.png')} style={styles.image} />

                {/* <Text style={styles.text}>Lon: {humidity}</Text> */}
                <Text style={styles.text}>City: {name}</Text>
                <Text style={styles.text}>{temp}</Text>
                <Text style={styles.text}>{description}</Text>
                <Text style={styles.text}>{icon}</Text>
            </View>
        </LinearGradient>
    )
}

export default Main

const styles = StyleSheet.create({
    container: {
        flex: 1,
        // alignItems: 'center',
        // justifyContent: 'center',
    },
    weatherContainer: {
        backgroundColor: 'rgba(255, 255, 255,0.3)',
        height: '100px',
        padding: '50px',
        width: '100px',
    },
    text: {
        fontSize: 36,
        color: 'white'
    },
    image: {
        left: 0,
        top: 25,
        height: 75,
        width: 75,
        marginBottom: 20
    }


})
