import React from 'react'
import { View, Text, Image, StyleSheet } from 'react-native'

const DailyForecast = () => {
    return (
        <View style={{flexDirection:'row'}}>
            <DailyForecastItem></DailyForecastItem>
            <DailyForecastItem></DailyForecastItem>
            <DailyForecastItem></DailyForecastItem>
        </View>
    )
}

const DailyForecastItem = () => {
    const img = require('../assets/sunny.png')
    return (
        <View style={styles.dailyForecastItemContainer}>
            <Text style={styles.day}>Monday</Text>
            <Image source={img} style={styles.image}></Image>
            <Text style={styles.temp}>Night - 26&#176;</Text>
            <Text style={styles.temp}>Day - 36&#176;</Text>
        </View>
    )
}

export default DailyForecast

const styles = StyleSheet.create({
    image: {
        width: 70,
        height: 70,
        marginLeft: 10
    },
    dailyForecastItemContainer: {
        flex: 1,
        justifyContent: 'center',
        backgroundColor: '#00000033',
        borderRadius: 10,
        borderColor: 'white',
        borderWidth: 1,
        padding: 20,
        // marginLeft: 10,
        marginRight: 40
    },
    temp: {
        fontSize: 16,
        color: 'white',
        fontWeight: '100',
        textAlign: 'center'
    },
    day: {
        fontSize: 20,
        color: 'white',
        backgroundColor: '#3c3c44',
        padding: 10,
        textAlign: 'center',
        borderRadius: 50,
        fontWeight: '200',
        marginBottom: 15,
    }
})
