import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet, Image, ImageBackground, Dimensions, StatusBar } from 'react-native'
import SearchBar from './SearchBar';
import { haze, rainy, snow, sunny } from '../assets/backgroundImages/index'
import { LinearGradient } from 'expo-linear-gradient';

export default function Weather({ weatherData }) {
    const [background, setBackground] = useState(null)
    const { weather,
        name,
        main: { temp, humidity } } = weatherData;
    const [{ main }] = weather;
    const icon = weatherData.weather[0].icon;

    React.useEffect(() => {
        setBackground(getBackground(main));
    }, [weatherData])

    function getBackground(weather) {
        if (weather === "Snow") return sunny
        if (weather === "Haze") return sunny
        if (weather === "Rain") return sunny
        if (weather === "Clear") return sunny

        return rainy;
    }

    return (
        <View style={styles.container}>
            <LinearGradient
                // Background Linear Gradient
                colors={['rgba(0,0,0,0.8)', 'transparent']}
                style={styles.background}
            />
            <LinearGradient
                // Button Linear Gradient
                colors={['#4c669f', '#3b5998', '#192f6a']}
                style={styles.button}>
                <Text style={styles.text}>Sign in with Facebook</Text>
            </LinearGradient>
        </View>
    )
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
