        <View style={styles.weatherContainer}>
            <View style={{backgroundColor: 'black'}}>
                
                <Image style={styles.image} source={{ uri: '../../assets/rain.png' }} />
            </View>
        </View>
    )
}

export default Main

const styles = StyleSheet.create({
    container: {
