import React, { useEffect, useState } from 'react'
import Background from './background/Background'
import { Image, View, StyleSheet, Text, Button, Dimensions } from 'react-native'
import { LinearGradient } from 'expo-linear-gradient';


const Main = ({ data }) => {
    const { weather,
        name,
        main: { temp, humidity },
        wind: { speed }
    } = data;
    const [{ main, description, icon }] = weather;
    // const[{description}] = weather;

    return (
        <View style={styles.container}>
            <Background props={
                <View>
                    <Text style={{}}>Lviv</Text>
                    <Image source={require('../../assets/cloud.png')} style={styles.image} />

                    {/* <Text style={styles.text}>City: {name}</Text>
                    <Text style={styles.text}>{temp}</Text>
                    <Text style={styles.text}>{description}</Text>
                    <Text style={styles.text}>{icon}</Text> */}
                    <View style={styles.extraInfo}>

                    <View style={styles.info}>
                        <Text style={{ fontSize: 22, color: 'white' }}>Humidity</Text>
                        <Text style={{ fontSize: 22, color: 'white' }}>{humidity} %</Text>
                    </View>

                    <View style={styles.info}>
                        <Text style={{ fontSize: 22, color: 'white' }}>{temp}</Text>
                    </View>
                    
                
                </View>
                </View>
            } />
        </View>
    )
}

export default Main

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        // justifyContent: 'center',
    },
    weatherContainer: {
        backgroundColor: 'rgba(255, 255, 255,0.3)',
        height: '100px',
        padding: '50px',
        width: '100px',
    },
    text: {
        fontSize: 36,
        color: 'white',
        top: 50,
        left: 10,
    },
    image: {
        position: 'relative',
        right: 10,
        top: 25,
        height: 150,
        width: 150,
        marginBottom: 20,
    },
    extraInfo: {
        flexDirection: 'row',
        marginTop: 20,
        justifyContent: 'space-between',
        padding: 10,
        alignItems:'baseline',
        display: 'block'
    },
    info: {
        width: Dimensions.get('screen').width / 2.5,
        backgroundColor: 'rgba(255,255,255, 0.5)',
        padding: 10,
        borderRadius: 15,
        justifyContent: 'center'
    }
})
