import React, { useEffect, useState } from 'react'
import { View, Text, Image, StyleSheet } from 'react-native'
import moment from 'moment'
import { LinearGradient } from 'expo-linear-gradient'

const DailyForecast = ({ data }) => {
    return (
        <View style={{ flexDirection: 'row' }}>
            <DailyForecastItem data={data}></DailyForecastItem>
        </View>
    )
}

export default DailyForecast

const DailyForecastItem = ({ data }) => {
    const img = require('../assets/sunny.png')
    // const {current:{location}} = data;

    return (
        <>
            {data.forecast.forecastday.map((item, idx) =>
                <View style={styles.dailyForecastItemContainer} key={idx}>
                    <LinearGradient style={styles.dayGradient} colors={['#4e369e', '#b450e6']} start={{ x: 0, y: 0.75 }} end={{ x: 1, y: 0.25 }}  >
                        <Text style={styles.day}>{moment(item.date).format('dddd')}</Text>
                    </LinearGradient>
                    <Image source={{ uri: `http:${item.day.condition.icon}` }} style={styles.image}></Image>
                    <Text style={styles.temp}>Temp - {item.day.avgtemp_c}&#176;</Text>
                    <Text style={styles.temp}>Rain - {item.day.daily_chance_of_rain}%</Text>
                </View>)}
        </>
    )
}

const styles = StyleSheet.create({
    image: {
        width: 100,
        height: 100,
        marginLeft: 10
    },
    dailyForecastItemContainer: {
        flex: 1,
        justifyContent: 'center',
        // backgroundColor: '#00000033',
        backgroundColor: 'rgba(173, 172, 168,0.15)',
        borderRadius: 10,
        borderColor: 'white',
        borderWidth: 1,
        padding: 20,
        // marginLeft: 10,
        marginRight: 40
    },
    temp: {
        fontSize: 18,
        color: 'white',
        fontWeight: '100',
        textAlign: 'center'
    },
    dayGradient: {
        
        color: 'white',
        // backgroundColor: '#3c3c44',
        // backgroundColor: '#A076F9',
        borderRadius: 50,
        marginBottom: 15,
    },
    day: {
        padding: 10,
        textAlign: 'center', 
        fontWeight: '200',
        fontSize: 22,
        color: 'white'
    }
})
